import React from 'react';

// Компонент для отображения отдельной задачи
const TaskItem = ({ task, toggleTaskCompletion, deleteTask }) => {
    return (
        <li style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
            {/* Название задачи, которое можно кликнуть для изменения её состояния */}
            <span onClick={() => toggleTaskCompletion(task.id)}>{task.text}</span>
            {/* Кнопка для удаления задачи */}
            <button onClick={() => deleteTask(task.id)}>Удалить</button>
        </li>
    );
};

export default TaskItem; // Экспортируем компонент для использования в других файлах
