import React, { useState }  from 'react';
import './ComponentsStyles/JoinSubject.css';

const JoinSubject = ({ onClose, onJoin }) => {
    const [link, setLink] = useState('');

    const handleJoin = () => {
        if (link.trim()) {
            // Здесь должна быть логика проверки ссылки/кода
            // После успешной проверки создаем предмет с isCreator: false
            const joinedSubject = {
                title: `Предмет по ссылке ${link}`, // Временное название, должно приходить с бэкенда
                description: 'Описание предмета', // Должно приходить с бэкенда
                deadline: '2023-12-31', // Должно приходить с бэкенда
                isCreator: false // Важно - указываем что пользователь участник
            };
            onJoin(joinedSubject);
            onClose();
        }
    };
    return (
        <div className="joinsubject-join-subject-wrapper">
            <div className="joinsubject-join-subject-container">
                <h2 className="joinsubject-form-title">Присоединиться к предмету</h2>

                <div className="joinsubject-input-container">
                    <input
                        type="text"
                        placeholder="Ссылка"
                        className="joinsubject-input-field"
                    />
                </div>

                <div className="joinsubject-buttons-container">
                    <button className="joinsubject-back-button" onClick={onClose}>Назад</button>
                    <button className="joinsubject-join-button">Присоединиться</button>
                </div>
            </div>
        </div>
    );
};

export default JoinSubject;
