import React from 'react';
import { useLocation } from 'react-router-dom';
import './ComponentsStyles/ItemPage.css';
import TaskList from "./TaskList";// Создайте соответствующий CSS файл при необходимости

const ItemPage = () => {
    const location = useLocation();
    const { item } = location.state || {};

    if (!item) {
        return <div className="item-page-error">Элемент не найден</div>;
    }

    return (
        <div className="item-page-container">
            <h1 className="item-page-title">{item.title}</h1>

            <TaskList task={item} />

            {/* Дополнительные поля элемента можно добавить здесь */}
        </div>
    );
};

export default ItemPage;
