import React, { useEffect, useState } from 'react';
import CreateItemModal from './CreateItemModal';
import ItemList from './ItemList';
import JoinSubject from './JoinSubject';
import './ComponentsStyles/Object.css';
import { createSubject, getSubjects } from "../api";

const Object = () => {

    const [items, setItems] = useState([]);
    const [isModalOpen, setModalOpen] = useState(false);
    const [isJoinModalOpen, setIsJoinModalOpen] = useState(false);

    useEffect(() => {
        async function fetchData() {
            const responseSub = await getSubjects();
            setItems(responseSub.data);
            console.log(responseSub.data);
        }
        fetchData();
    }, []);

    const handleOpenModal = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    const handleAddItem = (title, description, deadline) => {
        // РЎРѕР·РґР°РµРј РЅРѕРІС‹Р№ СЌР»РµРјРµРЅС‚
        const newItem = { title, description, deadline };
        
        // РћР±РЅРѕРІР»СЏРµРј СЃРѕСЃС‚РѕСЏРЅРёРµ items Р»РѕРєР°Р»СЊРЅРѕ
        setItems((prevItems) => [...prevItems, newItem]);

        // Р—Р°РєСЂС‹РІР°РµРј РјРѕРґР°Р»СЊРЅРѕРµ РѕРєРЅРѕ
        handleCloseModal();

        // Р•СЃР»Рё РЅРµРѕР±С…РѕРґРёРјРѕ, РјРѕР¶РЅРѕ СЃРґРµР»Р°С‚СЊ Р·Р°РїСЂРѕСЃ Рє API РґР»СЏ СЃРѕС…СЂР°РЅРµРЅРёСЏ РЅРѕРІРѕРіРѕ РїСЂРµРґРјРµС‚Р°
        // await createSubject(title, description, deadline);
    };

    // Р¤СѓРЅРєС†РёСЏ РґР»СЏ СѓРґР°Р»РµРЅРёСЏ РїСЂРµРґРјРµС‚Р°
    const handleDeleteItem = (index) => {
        setItems((prevItems) => prevItems.filter((_, i) => i !== index));
    };

    const handleOpenJoin = () => {
        setIsJoinModalOpen(true);
    };

    const handleCloseJoin = () => {
        setIsJoinModalOpen(false);
    };

    return (
        <div>
            <h1 className="object-object-title">РџСЂРµРґРјРµС‚С‹</h1>
            <button className="object-create-item" onClick={handleOpenModal}>РЎРѕР·РґР°С‚СЊ РїСЂРµРґРјРµС‚</button>
            <button className="object-join" onClick={handleOpenJoin}>РџСЂРёСЃРѕРµРґРёРЅРёС‚СЊСЃСЏ</button>

            {/* РџРµСЂРµРґР°РµРј onDelete */}
            <ItemList items={items} onDelete={handleDeleteItem} />

            {isModalOpen && (
                <CreateItemModal onClose={handleCloseModal} onAddItem={handleAddItem} />
            )}

            {isJoinModalOpen && (<JoinSubject onClose={handleCloseJoin}/>)}
        </div>
    );
};

export default Object;
