import React from 'react';
import './ComponentsStyles/ItemList.css'; // Импортируйте CSS-файл, если он есть

const ItemList = ({ items, onDelete }) => {
    return (
        <div className="it-item-list-container">
            <ul className="it-item-list">
                {items.map((item, index) => (
                    <li key={index} className="it-item">
                        <div className="it-item-headr">
                            <h3 className="it-item-name">{item.name}</h3>
                            {item.description && (
                                <p className="it-item-description"><strong>Описание:</strong> {item.description}</p>
                            )}
                            {item.deadline && (
                                <p className="it-item-deadline"><strong>Дедлайн:</strong> {item.deadline}</p>
                            )}
                        </div>
                        <button className="it-delete-button" onClick={() => onDelete(index)}>Удалить</button>
                        <button className="it-go-button">Перейти</button>

                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ItemList;
