import React, { useEffect, useState } from 'react';
import CreateItemModal from './CreateItemModal';
import ItemList from './ItemList';
import JoinSubject from './JoinSubject';
import './ComponentsStyles/Object.css';
import { createSubject, getSubjects } from "../api";

const Object = () => {

    const [items, setItems] = useState([]);
    const [isModalOpen, setModalOpen] = useState(false);
    const [isJoinModalOpen, setIsJoinModalOpen] = useState(false);

    useEffect(() => {
        async function fetchData() {
            const responseSub = await getSubjects();
            setItems(responseSub.data);
            console.log(responseSub.data);
        }
        fetchData();
    }, []);

    const handleOpenModal = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    const handleAddItem = (title, description, deadline) => {
        // Создаем новый элемент
        const newItem = { title, description, deadline, isCreator: true }; //Добавляем флаг создателя
        
        // Обновляем состояние items локально
        setItems((prevItems) => [...prevItems, newItem]);

        // Закрываем модальное окно
        handleCloseModal();

        // Если необходимо, можно сделать запрос к API для сохранения нового предмета
        // await createSubject(title, description, deadline);
    };

    // Функция для удаления предмета
    const handleDeleteItem = (index) => {
        setItems((prevItems) => prevItems.filter((_, i) => i !== index));
    };

    const handleOpenJoin = () => {
        setIsJoinModalOpen(true);
    };

    const handleCloseJoin = () => {
        setIsJoinModalOpen(false);
    };

    return (
        <div>
            <h1 className="object-object-title">Предметы</h1>
            <button className="object-create-item" onClick={handleOpenModal}>Создать предмет</button>
            <button className="object-join" onClick={handleOpenJoin}>Присоединиться</button>

            {/* Передаем onDelete */}
            <ItemList items={items} onDelete={handleDeleteItem} />

            {isModalOpen && (
                <CreateItemModal onClose={handleCloseModal} onAddItem={handleAddItem} />
            )}

            {isJoinModalOpen && (<JoinSubject onClose={handleCloseJoin} onJoin={(newItem) => setItems(prev => [...prev, newItem])}/>)}
        </div>
    );
};

export default Object;
