import React from 'react';
import { useLocation } from 'react-router-dom';
import './ComponentsStyles/ItemPage.css'; // Создайте соответствующий CSS файл при необходимости

const ItemPage = () => {
    const location = useLocation();
    const { item } = location.state || {};

    if (!item) {
        return <div className="item-page-error">Элемент не найден</div>;
    }

    return (
        <div className="item-page-container">
            <h1 className="item-page-title">{item.title}</h1>

            {item.description && (
                <p className="item-page-description">
                    <strong>Описание:</strong> {item.description}
                </p>
            )}

            {item.deadline && (
                <p className="item-page-deadline">
                    <strong>Дедлайн:</strong> {item.deadline}
                </p>
            )}

            {/* Дополнительные поля элемента можно добавить здесь */}
        </div>
    );
};

export default ItemPage;
