import React, { useState } from 'react';
import CreateTaskForm from './CreateTaskForm';
import ItemList from './ItemList';
import './App.css'; // Импортируйте стили для вашего приложения

const App = () => {
    const [isModalOpen, setModalOpen] = useState(false);
    const [tasks, setTasks] = useState([]);

    const handleOpenModal = () => {
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    const handleCreateTask = (newTask) => {
        setTasks((prevTasks) => [...prevTasks, newTask]);
    };

    return (
        <div className="app-container">
            <h1>Личные задачи</h1>
            <button onClick={handleOpenModal}>Создать задачу</button>

            {/* Отображение списка задач */}
            <ItemList tasks={tasks} />

            {/* Модальное окно с формой создания задачи */}
            {isModalOpen && (
                <CreateTaskForm onClose={handleCloseModal} onCreate={handleCreateTask} />
            )}
        </div>
    );
};

export default App;
