import React from 'react';
import { useLocation } from 'react-router-dom';

const ItemPage = () => {
    const location = useLocation();
    const item = location.state.item; // Получаем переданные данные о элементе через состояние

    return (
        <div className="item-page">
            <h1>{item.title}</h1> {/* Используем условную цепочку для безопасного доступа к title */}
            {/* Здесь можно добавить больше информации о элементе */}
        </div>
    );
};

export default ItemPage;
