import React from 'react';

const TaskList = ({ tasks, onEdit, onDelete }) => {
    return (
        <div className="task-list">
            <ul>
                {tasks.map((task, index) => (
                    <li key={index} className="task-item">
                        <div className="task-header">
                            <h3 className="task-title">{task.title}</h3>
                            <div className="task-buttons">
                                <button className="edit-button" onClick={() => onEdit(task)}>Редактировать</button>
                                <button className="delete-button" onClick={() => onDelete(task)}>Удалить</button>
                            </div>
                        </div>
                        <p className="task-description">{task.description}</p>
                        <p className="task-deadline">Срок начала: {task.deadlineStart}</p>
                        <p className="task-deadline">Срок окончания: {task.deadlineEnd}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TaskList;
