// EditTask.js
import React, { useEffect, useState } from 'react';
import { updateSubjectTask } from '../api';
import './ComponentsStyles/EditTask.css';

const EditTask = ({ task, onClose, onEdit }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        if (task) {
            setTitle(task.title || '');
            setDescription(task.description || '');
            // Форматируем даты для input type="date" (YYYY-MM-DD)
            setStartDate(formatDateForInput(task.deadlineStart) || '');
            setEndDate(formatDateForInput(task.deadlineEnd) || '');
        }
    }, [task]);

    // Функция для форматирования даты в формат YYYY-MM-DD
    const formatDateForInput = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return date.toISOString().split('T')[0];
    };

    const handleSaveChanges = async () => {
        if (!title.trim()) {
            setError('Название задачи обязательно');
            return;
        }

        if (!startDate || !endDate) {
            setError('Укажите даты начала и окончания');
            return;
        }

        if (new Date(startDate) > new Date(endDate)) {
            setError('Дата окончания не может быть раньше даты начала');
            return;
        }

        const updatedTask = {
            title: title.trim(),
            description: description.trim(),
            deadlineStart: startDate,
            deadlineEnd: endDate,
        };

        setIsLoading(true);
        setError('');

        try {
            await updateSubjectTask(task.subjectId, task.id, updatedTask);
            onEdit(updatedTask);
            onClose();
        } catch (err) {
            console.error('Ошибка при обновлении задачи:', err);
            setError('Не удалось сохранить изменения. Пожалуйста, попробуйте снова.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="edittask-edit-task-wrapper">
            <div className="edittask-edit-task-container">
                <h2 className="edittask-form-title">Редактирование задачи</h2>

                {error && <div className="edittask-error-message">{error}</div>}

                <input
                    type="text"
                    className="edittask-input-field"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Название задачи"
                    disabled={isLoading}
                />

                <textarea
                    className="edittask-textarea-field"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Описание задачи"
                    disabled={isLoading}
                />

                <div className="edittask-date-fields">
                    <label>
                        Дата окончания:
                        <input
                            type="date"
                            className="edittask-input-field"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                            disabled={isLoading}
                        />
                    </label>
                </div>

                <div className="edittask-buttons-container">
                    <button
                        className="edittask-cancel-button"
                        onClick={onClose}
                        disabled={isLoading}
                    >
                        Отмена
                    </button>
                    <button
                        className="edittask-save-button"
                        onClick={handleSaveChanges}
                        disabled={isLoading}
                    >
                        {isLoading ? 'Сохранение...' : 'Сохранить'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default EditTask;
