import React from 'react';

const ItemList = ({ items, onDeleteItem }) => {
    return (
        <ul>
            {items.map((item, index) => (
                <li key={index}>
                    {item} {/* Отображение имени предмета */}
                    <button onClick={() => onDeleteItem(index)}>Удалить</button> {/* Кнопка удаления */}
                </li>
            ))}
        </ul>
    );
};

export default ItemList;
