import React from 'react';

// Компонент для навигации
const Navigation = () => {
    return (
        <div style={{
            backgroundColor: 'green', // Зелёный фон
            padding: '10px', // Отступы внутри рамки
            display: 'flex', // Используем flexbox для выравнивания кнопок
            justifyContent: 'space-around', // Распределяем кнопки по ширине
            width: '100%', // Ширина на 100% для заполнения всей длины экрана
            boxSizing: 'border-box' // Учитываем отступы в ширине
        }}>
            <button style={{ color: 'black', background: 'transparent', border: 'none' }}>Главная</button>
            <button style={{ color: 'black', background: 'transparent', border: 'none' }}>Предметы</button>
            <button style={{ color: 'black', background: 'transparent', border: 'none' }}>Личные задачи</button>
        </div>
    );
};

export default Navigation; // Экспортируем компонент для использования в других файлах
