import React, { useState } from "react";

const CreateItemModal = ({ isOpen, onClose, onCreate }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [deadline, setDeadline] = useState("");

    const handleCreate = () => {
        if (!title || !description || !deadline) {
            alert("Пожалуйста, заполните все поля!");
            return;
        }

        // Передаем данные нового предмета обратно в родительский компонент
        onCreate({ title, description, deadline });

        // Сбрасываем поля формы
        setTitle("");
        setDescription("");
        setDeadline("");

        // Закрываем модальное окно
        onClose();
    };

    if (!isOpen) {
        return null; // Если модальное окно закрыто, ничего не рендерим
    }

    return (
        <div style={styles.overlay}>
            <div style={styles.modal}>
                <h2>Создание предмета</h2>
                <input
                    type="text"
                    placeholder="Название"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    style={styles.input}
                />
                <input
                    type="text"
                    placeholder="Описание"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    style={styles.input}
                />
                <input
                    type="date"
                    value={deadline}
                    onChange={(e) => setDeadline(e.target.value)}
                    style={styles.input}
                />
                <div style={styles.buttons}>
                    <button onClick={handleCreate} style={styles.createButton}>
                        Создать
                    </button>
                    <button onClick={onClose} style={styles.cancelButton}>
                        Отмена
                    </button>
                </div>
            </div>
        </div>
    );
};

const styles = {
    overlay: {
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
    },
    modal: {
        backgroundColor: "#fff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 5px 15px rgba(0, 0, 0, 0.3)",
        width: "400px",
        textAlign: "center",
    },
    input: {
        width: "100%",
        padding: "10px",
        margin: "10px 0",
        borderRadius: "5px",
        border: "1px solid #ccc",
    },
    buttons: {
        display: "flex",
        justifyContent: "space-between",
        marginTop: "20px",
    },
    createButton: {
        backgroundColor: "#4CAF50",
        color: "#fff",
        border: "none",
        padding: "10px 20px",
        borderRadius: "5px",
        cursor: "pointer",
    },
    cancelButton: {
        backgroundColor: "#f44336",
        color: "#fff",
        border: "none",
        padding: "10px 20px",
        borderRadius: "5px",
        cursor: "pointer",
    },
};

export default CreateItemModal;
